import React, { Component, useState } from "react";

class Header extends Component {
  render() {
    const [query, setQuery] = useState("");

    const onChange = (e) => {
      e.preventDefault();
      setQuery(e.target.value);
    };
    return (
      <div className="back">
        <h1> Movies & TV </h1>
        <form>
          <input
            type="text"
            name="search"
            placeholder="Search.."
            value={query}
            onChange={onChange}
          ></input>
        </form>
        <button>LOG IN</button>
      </div>
    );
  }
}
export default Header;
